INSERT INTO messages (id, sender_id, content, channel_id, created_at) VALUES
  ('44444444-4444-4444-4444-444444444401', '11111111-1111-1111-1111-111111111105', 'Welcome to DevCollab! We''re excited to have everyone onboard for the new sprint.', '22222222-2222-2222-2222-222222222201', '2026-02-10 09:00:00+00'),
  ('44444444-4444-4444-4444-444444444402', '11111111-1111-1111-1111-111111111102', 'Thanks Riley! Looking forward to collaborating with the team.', '22222222-2222-2222-2222-222222222201', '2026-02-10 09:05:00+00'),
  ('44444444-4444-4444-4444-444444444403', '11111111-1111-1111-1111-111111111103', 'Just pushed the new design system tokens. Let me know if anyone has questions.', '22222222-2222-2222-2222-222222222201', '2026-02-10 09:15:00+00'),
  ('44444444-4444-4444-4444-444444444404', '11111111-1111-1111-1111-111111111101', 'Great work Jordan. I''ll review those this afternoon.', '22222222-2222-2222-2222-222222222201', '2026-02-10 09:22:00+00'),
  ('44444444-4444-4444-4444-444444444405', '11111111-1111-1111-1111-111111111106', 'CI pipeline is green across all environments. Ship it!', '22222222-2222-2222-2222-222222222201', '2026-02-10 09:30:00+00'),
  ('44444444-4444-4444-4444-444444444406', '11111111-1111-1111-1111-111111111104', 'The API refactor is complete. Docs are updated in the engineering channel.', '22222222-2222-2222-2222-222222222201', '2026-02-10 10:00:00+00');
INSERT INTO messages (id, sender_id, content, channel_id, created_at) VALUES
  ('44444444-4444-4444-4444-444444444407', '11111111-1111-1111-1111-111111111104', 'I''ve refactored the auth middleware. PR #247 is ready for review.', '22222222-2222-2222-2222-222222222202', '2026-02-10 08:30:00+00'),
  ('44444444-4444-4444-4444-444444444408', '11111111-1111-1111-1111-111111111101', 'Looking at it now. The token refresh logic looks much cleaner.', '22222222-2222-2222-2222-222222222202', '2026-02-10 08:45:00+00'),
  ('44444444-4444-4444-4444-444444444409', '11111111-1111-1111-1111-111111111108', 'Should we migrate to the new ORM? I''ve been benchmarking and it''s 3x faster.', '22222222-2222-2222-2222-222222222202', '2026-02-10 09:10:00+00'),
  ('44444444-4444-4444-4444-444444444410', '11111111-1111-1111-1111-111111111106', 'Let''s discuss in standup. I have some concerns about migration risk.', '22222222-2222-2222-2222-222222222202', '2026-02-10 09:20:00+00'),
  ('44444444-4444-4444-4444-444444444411', '11111111-1111-1111-1111-111111111102', 'Frontend bundle size is down 22% after tree-shaking improvements.', '22222222-2222-2222-2222-222222222202', '2026-02-10 09:45:00+00');
INSERT INTO messages (id, sender_id, content, channel_id, created_at) VALUES
  ('44444444-4444-4444-4444-444444444412', '11111111-1111-1111-1111-111111111103', 'New component library preview is live. Check the Figma link in the thread.', '22222222-2222-2222-2222-222222222203', '2026-02-10 10:00:00+00'),
  ('44444444-4444-4444-4444-444444444413', '11111111-1111-1111-1111-111111111105', 'The new dashboard layout is looking sharp. Great work on the spacing.', '22222222-2222-2222-2222-222222222203', '2026-02-10 10:15:00+00'),
  ('44444444-4444-4444-4444-444444444414', '11111111-1111-1111-1111-111111111107', 'Found a couple of contrast issues on the dark theme. I''ll file tickets.', '22222222-2222-2222-2222-222222222203', '2026-02-10 10:30:00+00');
INSERT INTO messages (id, sender_id, content, channel_id, created_at) VALUES
  ('44444444-4444-4444-4444-444444444415', '11111111-1111-1111-1111-111111111105', 'Team all-hands is next Thursday at 2 PM. Please mark your calendars.', '22222222-2222-2222-2222-222222222204', '2026-02-10 08:00:00+00'),
  ('44444444-4444-4444-4444-444444444416', '11111111-1111-1111-1111-111111111105', 'Q1 goals have been published. Check the wiki for your team''s OKRs.', '22222222-2222-2222-2222-222222222204', '2026-02-10 11:00:00+00');
INSERT INTO messages (id, sender_id, content, chat_id, created_at) VALUES
  ('55555555-5555-5555-5555-555555555501', '11111111-1111-1111-1111-111111111102', 'Hey Alex, are you free to pair on the auth module?', '33333333-3333-3333-3333-333333333301', '2026-02-10 10:00:00+00'),
  ('55555555-5555-5555-5555-555555555502', '11111111-1111-1111-1111-111111111101', 'Sure! How about 3 PM?', '33333333-3333-3333-3333-333333333301', '2026-02-10 10:15:00+00'),
  ('55555555-5555-5555-5555-555555555503', '11111111-1111-1111-1111-111111111102', 'Sounds good, let''s sync at 3.', '33333333-3333-3333-3333-333333333301', '2026-02-10 10:30:00+00');
INSERT INTO messages (id, sender_id, content, chat_id, created_at) VALUES
  ('55555555-5555-5555-5555-555555555504', '11111111-1111-1111-1111-111111111103', 'I''ve finished the onboarding flow mockups.', '33333333-3333-3333-3333-333333333302', '2026-02-10 09:00:00+00'),
  ('55555555-5555-5555-5555-555555555505', '11111111-1111-1111-1111-111111111101', 'Awesome, can you share the Figma link?', '33333333-3333-3333-3333-333333333302', '2026-02-10 09:20:00+00'),
  ('55555555-5555-5555-5555-555555555506', '11111111-1111-1111-1111-111111111103', 'The mockups are attached.', '33333333-3333-3333-3333-333333333302', '2026-02-10 09:45:00+00');
INSERT INTO messages (id, sender_id, content, chat_id, created_at) VALUES
  ('55555555-5555-5555-5555-555555555507', '11111111-1111-1111-1111-111111111101', 'Just reviewed your PR on the API refactor.', '33333333-3333-3333-3333-333333333303', '2026-02-09 18:00:00+00'),
  ('55555555-5555-5555-5555-555555555508', '11111111-1111-1111-1111-111111111104', 'Thanks! Any blockers?', '33333333-3333-3333-3333-333333333303', '2026-02-09 18:15:00+00'),
  ('55555555-5555-5555-5555-555555555509', '11111111-1111-1111-1111-111111111101', 'PR approved!', '33333333-3333-3333-3333-333333333303', '2026-02-09 18:30:00+00');
INSERT INTO messages (id, sender_id, content, chat_id, created_at) VALUES
  ('55555555-5555-5555-5555-555555555510', '11111111-1111-1111-1111-111111111105', 'Good morning team. Sprint 14 planning starts now.', '33333333-3333-3333-3333-333333333304', '2026-02-10 09:00:00+00'),
  ('55555555-5555-5555-5555-555555555511', '11111111-1111-1111-1111-111111111104', 'I''ve got the backend tasks estimated and ready.', '33333333-3333-3333-3333-333333333304', '2026-02-10 09:05:00+00'),
  ('55555555-5555-5555-5555-555555555512', '11111111-1111-1111-1111-111111111102', 'Frontend is also ready. We have 34 story points queued.', '33333333-3333-3333-3333-333333333304', '2026-02-10 09:10:00+00'),
  ('55555555-5555-5555-5555-555555555513', '11111111-1111-1111-1111-111111111106', 'Infra tasks are light this sprint, about 8 points.', '33333333-3333-3333-3333-333333333304', '2026-02-10 09:15:00+00'),
  ('55555555-5555-5555-5555-555555555514', '11111111-1111-1111-1111-111111111105', 'Let''s finalize the backlog today.', '33333333-3333-3333-3333-333333333304', '2026-02-10 11:00:00+00');
INSERT INTO messages (id, sender_id, content, chat_id, created_at) VALUES
  ('55555555-5555-5555-5555-555555555515', '11111111-1111-1111-1111-111111111103', 'Sharing the latest iteration of the dashboard.', '33333333-3333-3333-3333-333333333305', '2026-02-09 14:00:00+00'),
  ('55555555-5555-5555-5555-555555555516', '11111111-1111-1111-1111-111111111107', 'Tested on mobile and it looks great.', '33333333-3333-3333-3333-333333333305', '2026-02-09 14:30:00+00'),
  ('55555555-5555-5555-5555-555555555017', '11111111-1111-1111-1111-111111111105', 'New designs look great!', '33333333-3333-3333-3333-333333333305', '2026-02-09 15:00:00+00');
